import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker,
} from "react-simple-maps"

import {geoEqualEarth} from "d3-geo";

class App extends Component {
    render() {
        return (
            <div className="map-container">
                <ComposableMap
                    projection={geoEqualEarth}
                    projectionConfig={{ scale: 1 }}
                    width={980}
                    height={551}
                    style={{
                        width: "100%",
                        height: "auto",
                    }}
                >
                    <ZoomableGroup center={[ 90, 20 ]} disablePanning>
                        <Geographies  geography='./gadm36_IND.json'>
                            {(geographies, projection) =>
                                geographies.map((geography, i) =>
                                <Geography
                                key={i}
                                geography={geography}
                                projection={projection}
                                style={{
                                default: {
                                    fill: "#ECEFF1",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.75,
                                    outline: "none",
                                },
                                hover: {
                                    fill: "#CFD8DC",
                                    stroke: "#607D8B",
                                    strokeWidth: 1,
                                    outline: "none",
                                },
                                pressed: {
                                    fill: "#FF5722",
                                    stroke: "#607D8B",
                                    strokeWidth: 1,
                                    outline: "none",
                                }
                            }}
                                />
                                )
                            }
                        </ Geographies>
                    </ ZoomableGroup>
                </ ComposableMap>
            </div>
        )
    }
}

export default App;
