import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker,
} from "react-simple-maps"
import DropDownMenu from "./Components/dropDownMenu";
import DatePicker from 'react-datepicker';
import {faCalendarAlt} from "@fortawesome/free-solid-svg-icons/index";

var today = new Date()
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            startDate: today - 1000 * 60 * 60 * 24 * 1
        };
        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(date) {
        this.setState({
            startDate: date
        });
    }

    render() {
        return (
            <div className="Dashboard">
<div className = "App-header">

</div>
            <div className="map-container">
                <div className="barButtonGroup">
                    <div className="StateChart">
                        <span className="TopFilterLabel">Filter: </span>
                        State: <DropDownMenu />
                        <span>  </span>
                        Period:
                        <label className = "DatePicker">
                            <FontAwesomeIcon icon={faCalendarAlt} className="calendarIcon"/>
                            <DatePicker
                                className="DateText"
                                selected={this.state.startDate}
                                onSelect={this.handleSelect} //when day is clicked
                                onChange={this.handleChange} //only when value has changed
                            />
                        </label>
                        -
                        <label className = "DatePicker">
                            <FontAwesomeIcon icon={faCalendarAlt} className="calendarIcon"/>
                            <DatePicker
                                className="DateText"
                                selected={this.state.startDate}
                                onSelect={this.handleSelect} //when day is clicked
                                onChange={this.handleChange} //only when value has changed
                            />
                        </label>
                    </div>
                </div>
                <ComposableMap
                    projectionConfig={{ scale: 30 }}
                    width={30}
                    height={20}
                    style={{
                        width: "100%",
                        height: "auto",
                    }}
                >
                    <ZoomableGroup center={[ 80, 28]} disablePanning>
                        <Geographies  geography='./gadm36_IND.json'>
                            {(geographies, projection) =>
                                geographies.map((geography, i) =>
                                <Geography
                                key={i}
                                geography={geography}
                                projection={projection}
                                style={{
                                default: {
                                    fill: "#ECEFF1",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.05,
                                    outline: "none",
                                },
                                hover: {
                                    fill: "#CFD8DC",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.07,
                                    outline: "none",
                                },
                                pressed: {
                                    fill: "#FF5722",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.07,
                                    outline: "none",
                                }
                            }}
                                />
                                )
                            }
                        </ Geographies>
                    </ ZoomableGroup>
                </ ComposableMap>
            </div>
            </div>
        )
    }
}

export default App;
