import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker,
} from "react-simple-maps"
import DropDownMenu from "./Components/dropDownMenu";
import {geoEqualEarth} from "d3-geo";


class App extends Component {
    render() {
        return (
            <div className="Dashboard">
<div className = "App-header">

</div>
            <div className="map-container">
                <div className="barButtonGroup">
                    <div className="WeekBarChart">
                                        <span
                                            className="TopFilterLabel">Time </span>
                        <DropDownMenu opt={this.state.selectedStartWeek}
                                      options={this.state.WeekDropDown}
                                      click={this.onWeekStartChange.bind(this)}
                                      isMulti={false}/>
                        <span> - </span>
                        <DropDownMenu opt={this.state.selectedEndWeek}
                                      options={this.state.WeekDropDown}
                                      click={this.onWeekEndChange.bind(this)}
                                      isMulti={false}/>
                    </div>
                    <div className="CountryBarChart">
                        <span className="hdrDataFilter">Country </span>
                        <DropDownMenu opt={this.state.selectedOptionBar}
                                      options={this.props.countryList}
                                      click={this.onCountryChangeBar.bind(this)}
                                      isMulti={true}/>
                    </div>
                </div>
                <ComposableMap
                    projectionConfig={{ scale: 30 }}
                    width={30}
                    height={20}
                    style={{
                        width: "100%",
                        height: "auto",
                    }}
                >
                    <ZoomableGroup center={[ 80, 28]} disablePanning>
                        <Geographies  geography='./gadm36_IND.json'>
                            {(geographies, projection) =>
                                geographies.map((geography, i) =>
                                <Geography
                                key={i}
                                geography={geography}
                                projection={projection}
                                style={{
                                default: {
                                    fill: "#ECEFF1",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.05,
                                    outline: "none",
                                },
                                hover: {
                                    fill: "#CFD8DC",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.07,
                                    outline: "none",
                                },
                                pressed: {
                                    fill: "#FF5722",
                                    stroke: "#607D8B",
                                    strokeWidth: 0.07,
                                    outline: "none",
                                }
                            }}
                                />
                                )
                            }
                        </ Geographies>
                    </ ZoomableGroup>
                </ ComposableMap>
            </div>
            </div>
        )
    }
}

export default App;
